export var PasswordStrengthValidator = function PasswordStrengthValidator(control) {
  var value = control.value || '';

  if (!value) {
    return null;
  }

  var upperCaseCharacters = /[A-Z]+/g;

  if (upperCaseCharacters.test(value) === false) {
    return {
      passwordStrength: 'Password has to contain Upper case characters'
    };
  }

  var lowerCaseCharacters = /[a-z]+/g;

  if (lowerCaseCharacters.test(value) === false) {
    return {
      passwordStrength: 'Password has to contain lower case characters'
    };
  }

  var numberCharacters = /[0-9]+/g;

  if (numberCharacters.test(value) === false) {
    return {
      passwordStrength: 'Password has to contain number characters'
    };
  }

  var specialCharacters = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+/;

  if (specialCharacters.test(value) === false) {
    return {
      passwordStrength: 'Password has to contain a special character'
    };
  }

  return null;
};
